search.index:
function (doc) {
 if (doc.name) {
   index("name", doc.name);
   index("full_name", doc.full_name);
   index("description", doc.description);
   index("body", doc.body);
   var def = [ doc.name, doc.full_name, doc.description, doc.body, doc.languages.join(" "), doc.technologies.join(" "), doc.topic.join(" "), doc.type, doc.level];
   index("default", def.join(" "));
   for (var i in doc.languages) {
     index("language", doc.languages[i], { facet:true});
   }  
   for (var i in doc.technologies) {
     index("technology", doc.technologies[i], { facet:true});
   }
   for (var i in doc.topic) {
     index("topic", doc.topic[i], { facet:true});
   }
   index("type", doc.type, {facet:true});
   index("date", doc.created_at); 
    index("level", doc.level, {facet:true});
 }
};
